import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';

const AddCourse = () => {
  const [course, setCourse] = useState({
    cid: '',
    name: '',
    instructors: '',
    credits: ''
  });

  const handleChange = (e) => {
    setCourse({ ...course, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/courses', course);
      console.log(response.data);
      // Handle success
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  const handleEdit = async (id) => {
    // Logic to handle edit
  };

  const handleDelete = async (id) => {
    try {
      const response = await axios.delete(`/api/courses/${id}`);
      console.log(response.data);
      // Handle success
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  return (
    <div>

        <div>
            <Navbar/>
        </div>
        <div>
            Add Timeslot
        </div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="cid">Course Number:</label>
        <input
          type="number"
          name="cid"
          value={course.cid}
          onChange={handleChange}
        />

        <label htmlFor="name">Course Name:</label>
        <input
          type="text"
          name="name"
          value={course.name}
          onChange={handleChange}
        />

        <label htmlFor="instructors">Instructors:</label>
        <input
          type="text"
          name="instructors"
          value={course.instructors}
          onChange={handleChange}
        />

        <label htmlFor="credits">Credits:</label>
        <input
          type="number"
          name="credits"
          value={course.credits}
          onChange={handleChange}
        />

        <button type="submit">Submit</button>
      </form>
      {/* Additional form or UI elements for editing and deleting courses */}
    </div>
  );
};

export default AddCourse;
