// frontend/src/components/AddTeacher.js

import React, { useState } from 'react';
import Navbar from './Navbar';
import axios from 'axios';

const AddTeacher = () => {
  const [teacher, setTeacher] = useState({
    tid: '',
    name: ''
  });

  const handleChange = (e) => {
    setTeacher({ ...teacher, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/teachers', teacher);
      console.log(response.data);
      // Optionally, clear the form or give user feedback
    } catch (error) {
      console.error(error.response.data);
      // Handle errors, perhaps show user feedback
    }
  };

  return (
    <div>
        <div>
            <Navbar/>
        </div>
        <div>
            Add Teacher
        </div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="tid">Teacher ID:</label>
        <input
          type="number"
          name="tid"
          value={teacher.tid}
          onChange={handleChange}
        />

        <label htmlFor="name">Name:</label>
        <input
          type="text"
          name="name"
          value={teacher.name}
          onChange={handleChange}
        />

        <button type="submit">Add Teacher</button>
      </form>
    </div>
  );
};

export default AddTeacher;
