// frontend/src/SignUp.js
import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons';
import './SignUp.css'; // Import the CSS file




const SignUp = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [mobile, setMobile] = useState('');
  const [showPassword, setShowPassword] = useState('');
  const [mobileError, setMobileError] = useState('');
  const [signupError, setSignupError] = useState('');

  const handleSignUp = async () => {
    try {
      // Ensure all fields are filled before making the API call
      if (!username || !password || !mobile) {
        alert('All fields are mandatory');
        return;
      }

      const apiUrl = 'http://localhost:5000/api/signup';
      await axios.post(apiUrl, { username, password, mobile });
      console.log('User registered successfully');
      setSignupError('');
    } catch (error) {
      console.error('Error registering user:', error);

      if (error.response) {
        // Handle specific error messages from the server

        if (error.response.status === 400) {
          setSignupError('Username is already in use');
          setMobileError('Mobile number is already in use');
        }
        else if (error.response.status === 409)
        {
          setMobileError('Mobile number is already in use');
        }
        else if(error.response.status === 422)
        {
          setSignupError('Username is already in use');
        }
      } else {
        // Handle generic error messages
        setSignupError('Error registering user. Please try again later.');
      }
    }
  };

  const handleUserChange= (e) => {
      setSignupError('');

    setUsername(e.target.value);
  }

  const handleMobileChange = (e) => {
    var inputValue = e.target.value;
    var last= inputValue.at(-1);
    inputValue = e.target.value.replace(/[^0-9]/g, '').slice(0, 10);
    if(last>=0 && last<=9)
    {
      setMobileError('');
      setSignupError('');
    }

    setMobile(inputValue);
  };

  const handleMobileBlur = () => {
    // Validate mobile number length after blur
    if (mobile.length !== 10) {
      setMobileError('Mobile number must be 10 digits long');
    } else {
      setMobileError('');
    }
  };


  return (
    <div className="SignUpContainer">
      <h2>Sign Up</h2>
      <label>Username:</label>
      <input type="text" value={username} onChange={handleUserChange} required />
      <label>Password:</label>
      <div className="password-input-container">
        <input
          type={showPassword ? 'text' : 'password'}
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <FontAwesomeIcon
          icon={showPassword ? faEye : faEyeSlash}
          className="password-toggle-icon"
          onClick={() => setShowPassword(!showPassword)}
        />
      </div>
      <label>Mobile:</label>
      <input
        type="text"
        value={mobile}
        onChange={handleMobileChange}
        onBlur={handleMobileBlur}
        required
      />
      {mobileError && <p className="error-message">{mobileError}</p>}
      {signupError && <p className="error-message">{signupError}</p>}
      <button onClick={handleSignUp}>Sign Up</button>
    </div>
  );
};

export default SignUp;
