import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';
import Navbar from './Navbar';

const AddCourse = () => {
  const [teachers, setTeachers] = useState([]);
  const [selectedTeachers, setSelectedTeachers] = useState([]);
  const [selectedTeacherNames, setSelectedTeacherNames] = useState('');

  const [course, setCourse] = useState({
    cid: '',
    name: '',
    teachers: [], 
    credits: ''
  });

  const handleTeacherChange = (selectedOptions) => {
    // Update the selectedTeachers state
    setSelectedTeachers(selectedOptions);
    // Update the course state to include the IDs of the selected teachers
    setCourse({
      ...course,
      teachers: selectedOptions.map(option => option.value)
    });
  };

  const teacherOptions = teachers.map(teacher => ({
    label: teacher.name,
    value: teacher._id
  }));


  useEffect(() => {
    const fetchTeachers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/teachers');
        setTeachers(response.data);
      } catch (error) {
        console.error('Could not fetch teachers', error);
      }
    };

    fetchTeachers();
  }, []);

  const handleChange = (e) => {
    if (e.target.name === "teachers") {
      // For a <select multiple>, we need to iterate over the options and find the selected ones
      const selectedOptions = Array.from(e.target.options)
                                  .filter(option => option.selected)
                                  .map(option => option.value);
      setCourse(prevCourse => ({
        ...prevCourse,
        teachers: selectedOptions
      }));
    } else {
      setCourse(prevCourse => ({
        ...prevCourse,
        [e.target.name]: e.target.value
      }));
    }
  };
  
  

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/courses', course);
      console.log(response.data);
      // Handle success
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  const handleEdit = async (id) => {
    // Logic to handle edit
  };

  const handleDelete = async (id) => {
    try {
      const response = await axios.delete(`/api/courses/${id}`);
      console.log(response.data);
      // Handle success
    } catch (error) {
      console.error(error);
      // Handle error
    }
  };

  return (
    <div className="min-h-screen bg-gray-800 text-white flex flex-col items-center pt-10">
      <Navbar/>
      <h2 className="text-3xl font-bold mb-4">Add Course</h2>
      <form onSubmit={handleSubmit} className="w-full max-w-lg">
        <div className="mb-4">
          <label htmlFor="cid" className="block text-sm font-medium mb-1">Course Number:</label>
          <input
            type="number"
            name="cid"
            value={course.cid}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 bg-gray-700 text-white leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>

        <div className="mb-4">
          <label htmlFor="name" className="block text-sm font-medium mb-1">Course Name:</label>
          <input
            type="text"
            name="name"
            value={course.name}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 bg-gray-700 text-white leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>

        <div className="mb-4">
          <label htmlFor="teachers">Teachers:</label>
          <select
            name="teachers"
            value={course.teachers} // This should be an array of selected teacher IDs
            onChange={handleChange}
            multiple // Allows multiple selection
            className="shadow appearance-none border rounded w-full py-2 px-3 bg-gray-700 text-white leading-tight focus:outline-none focus:shadow-outline"
          >
            {teachers.map((teacher) => (
              <option key={teacher._id} value={teacher._id}>
                {teacher.name}
              </option>
            ))}
          </select>


        </div>

        <div className="mb-6">
          <label htmlFor="credits" className="block text-sm font-medium mb-1">Credits:</label>
          <input
            type="number"
            name="credits"
            value={course.credits}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 bg-gray-700 text-white leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>

        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddCourse;
